mapper.select:
SELECT count(*) FROM t_user d
LEFT JOIN t_permission p ON d.role_type = p.id
LEFT JOIN t_user_department td ON td.user_id=d.ID_
LEFT JOIN ACT_ID_MEMBERSHIP ug ON ug.USER_ID_ = d.ID_
WHERE d.id_ <> '-1' and d.status = '1'
AND ( d.FIRST_ like CONCAT('%',#{searchstr},'%') escape '*' or d.user_name like CONCAT('%',#{searchstr},'%') escape '*' )
and td.department_id=#{value}
and d.role_type=#{value}
and ug.group_id_=#{value}
AND d.role_type=#{permissionId}

SELECT DISTINCT(d.ID_) id, FIRST_ FIRST, EMAIL_ email, PWD_ pwd, user_name username, d.department, (SELECT GROUP_CONCAT(rp.`name`) FROM t_user_department ud LEFT JOIN t_department rp ON ud.department_id = rp.id WHERE ud.user_id = d.ID_) AS departmentNames, (SELECT GROUP_CONCAT(gp.NAME_) FROM ACT_ID_MEMBERSHIP gd LEFT JOIN ACT_ID_GROUP gp ON gd.GROUP_ID_ = gp.id_ WHERE gd.USER_ID_ = d.ID_) AS groupNames, role_type roleType, p.name roleTypeName, phone, status FROM ACT_ID_USER d LEFT JOIN t_permission p ON d.role_type = p.id WHERE d.id_ <> '-1' and d.status = '1'
AND (d.FIRST_ like CONCAT('%',#{searchstr},'%') escape '*' or d.user_name like CONCAT('%',#{searchstr},'%') escape '*')
AND role_type=#{permissionId}
order by user_name
limit ${startNum},${pageSize}

SELECT DISTINCT ID_ id, FIRST_ FIRST, EMAIL_ email, PWD_ pwd, user_name username, d.department, (SELECT GROUP_CONCAT(rp.`name`) FROM t_user_department ud LEFT JOIN t_department rp ON ud.department_id = rp.id WHERE ud.user_id = d.ID_) AS departmentNames, (SELECT GROUP_CONCAT(gp.NAME_) FROM ACT_ID_MEMBERSHIP gd LEFT JOIN ACT_ID_GROUP gp ON gd.GROUP_ID_ = gp.id_ WHERE gd.USER_ID_ = d.ID_) AS groupNames, role_type roleType, p.name roleTypeName, phone FROM ACT_ID_USER d LEFT JOIN t_permission p ON d.role_type = p.id LEFT JOIN t_user_department td ON td.user_id=d.ID_ LEFT JOIN ACT_ID_MEMBERSHIP ug ON ug.USER_ID_ = d.ID_ WHERE d.id_ <> '-1' and d.status = '1'
AND (d.id_ like CONCAT('%',#{searchstr},'%') or d.user_name like CONCAT('%',#{searchstr},'%') )
AND d.status=#{status}
and td.department_id=#{value}
and d.role_type=#{value}
and ug.group_id_=#{value}
order by user_name
limit ${startNum},${pageSize}

mapper.delete:
delete from t_user_department where user_id in

  #{item}


  and department_id = #{departmentId}

mapper.select:
SELECT DISTINCT(d.ID_) id, d.FIRST_ FIRST, d.EMAIL_ email, d.PWD_ pwd, user_name username, d. department, (SELECT GROUP_CONCAT(rp.`name`) FROM t_user_department ud LEFT JOIN t_department rp ON ud.department_id = rp.id WHERE ud.user_id = d.ID_) AS departmentNames, (SELECT GROUP_CONCAT(gp.NAME_) FROM ACT_ID_MEMBERSHIP gd LEFT JOIN ACT_ID_GROUP gp ON gd.GROUP_ID_ = gp.id_ WHERE gd.USER_ID_ = d.ID_) AS groupNames, d.role_type roleType, p.name roleTypeName, d.phone, d.status FROM ACT_ID_USER d LEFT JOIN t_permission p ON d.role_type = p.id LEFT JOIN t_user_department tud ON tud.user_id = d.ID_ LEFT JOIN t_department td ON td.id = tud.department_id WHERE d.id_ <> '-1' and d.status = '1'
AND (d.FIRST_ like CONCAT('%',#{searchstr},'%') escape "*" or d.user_name like CONCAT('%',#{searchstr},'%') escape "*" or td.name like CONCAT('%',#{searchstr},'%') escape "*")
AND role_type=#{permissionId}
order by user_name
limit #{startNum},#{pageSize}

